DECLARE
    l_data CLOB;
    json_sample clob; 
    l_url VARCHAR2(1000) := 'https://gw-apic-gov.gazt.gov.sa/e-invoicing/developer-portal/compliance/invoices' ;

BEGIN


    json_sample := json_object(
        'invoiceHash' value 't1vEblOtMk4E3+YFofL4rYw8ARqvuvA5aQYVw6wS3BA=',
        'uuid' value '16e78469-64af-406d-9cfd-895e724198f0',
        'invoice' value ''
    );

    

    apex_web_service.g_request_headers(1).name := 'Content-Type';
    apex_web_service.g_request_headers(1).value := 'application/json';


    apex_web_service.g_request_headers(2).name := 'Clearance-Status';
    apex_web_service.g_request_headers(2).value := '0';

    apex_web_service.g_request_headers(3).name := 'accept-language';
    apex_web_service.g_request_headers(3).value := 'en';

    apex_web_service.g_request_headers(4).name := 'authentication-certificate';
    apex_web_service.g_request_headers(4).value := '';



    

    l_data := apex_web_service.make_rest_request(
        p_url => l_url,
        p_http_method => 'POST',
        p_body        => json_sample
    );

    dbms_output.put_line(l_data);
    DELETE FROM API_RES;
    INSERT INTO API_RES VALUES(l_data);
    
END;